import React, { useState } from 'react';
import { predictFutureSkills } from '../services/api';

const FutureSkillPredictor = () => {
  const [skills, setSkills] = useState('');
  const [predictionResult, setPredictionResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handlePredict = async (e) => {
    e.preventDefault();
    
    setLoading(true);
    try {
      const response = await predictFutureSkills(skills);
      setPredictionResult(response.data);
    } catch (error) {
      console.error('Prediction error:', error);
      setPredictionResult({ error: 'Failed to predict future skills' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="future-skill-predictor">
      <h2>Future Skill Predictor (2-5 Year Forecast)</h2>
      <form onSubmit={handlePredict}>
        <div>
          <label htmlFor="skills">Current Skills (comma separated):</label>
          <input
            type="text"
            id="skills"
            value={skills}
            onChange={(e) => setSkills(e.target.value)}
            placeholder="e.g., Python, Machine Learning, Cloud Computing"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Predicting...' : 'Predict Future Skills'}
        </button>
      </form>
      
      {predictionResult && (
        <div className="prediction-result">
          <h3>Future Skills Forecast</h3>
          <p>Based on your current skills: {predictionResult.currentSkills.join(', ')}</p>
          
          <h4>Predicted In-Demand Skills:</h4>
          <div className="predictions">
            {predictionResult.predictions.map((prediction, index) => (
              <div key={index} className="prediction-item">
                <h5>{prediction.skill} ({prediction.timeframe}) - {prediction.importance}</h5>
                <p>{prediction.description}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default FutureSkillPredictor;
