import React, { useState } from 'react';
import { generatePortfolio } from '../services/api';

const PortfolioGenerator = () => {
  const [resumeData, setResumeData] = useState({
    name: '',
    email: '',
    phone: '',
    skills: '',
    experience: '',
    education: ''
  });
  const [generating, setGenerating] = useState(false);
  const [portfolio, setPortfolio] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setResumeData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    setGenerating(true);
    
    try {
      const response = await generatePortfolio(resumeData);
      setPortfolio(response.data);
    } catch (error) {
      console.error('Portfolio generation error:', error);
      setPortfolio({ error: 'Failed to generate portfolio' });
    } finally {
      setGenerating(false);
    }
  };

  return (
    <div className="portfolio-generator">
      <h2>AI Portfolio Generator</h2>
      <form onSubmit={handleGenerate}>
        <div>
          <label htmlFor="name">Full Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={resumeData.name}
            onChange={handleChange}
            required
          />
        </div>
        
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={resumeData.email}
            onChange={handleChange}
            required
          />
        </div>
        
        <div>
          <label htmlFor="phone">Phone:</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={resumeData.phone}
            onChange={handleChange}
          />
        </div>
        
        <div>
          <label htmlFor="skills">Skills (comma separated):</label>
          <input
            type="text"
            id="skills"
            name="skills"
            value={resumeData.skills}
            onChange={handleChange}
            placeholder="e.g., JavaScript, React, Node.js, Python"
          />
        </div>
        
        <div>
          <label htmlFor="experience">Experience:</label>
          <textarea
            id="experience"
            name="experience"
            value={resumeData.experience}
            onChange={handleChange}
            rows="4"
            placeholder="Describe your work experience..."
          />
        </div>
        
        <div>
          <label htmlFor="education">Education:</label>
          <textarea
            id="education"
            name="education"
            value={resumeData.education}
            onChange={handleChange}
            rows="3"
            placeholder="Describe your education background..."
          />
        </div>
        
        <button type="submit" disabled={generating}>
          {generating ? 'Generating Portfolio...' : 'Generate Portfolio'}
        </button>
      </form>
      
      {portfolio && (
        <div className="generated-portfolio">
          <h3>Generated Portfolio</h3>
          <div className="portfolio-content">
            <h4>{portfolio.name}</h4>
            <p>Email: {portfolio.email}</p>
            <p>Phone: {portfolio.phone}</p>
            
            <h5>Skills</h5>
            <ul>
              {portfolio.skills.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
            
            <h5>Experience</h5>
            <p>{portfolio.experience}</p>
            
            <h5>Education</h5>
            <p>{portfolio.education}</p>
            
            <h5>Projects</h5>
            {portfolio.projects.map((project, index) => (
              <div key={index} className="project">
                <h6>{project.title}</h6>
                <p>{project.description}</p>
                <p>Technologies: {project.technologies.join(', ')}</p>
              </div>
            ))}
            
            <div className="portfolio-link">
              <h5>Your Portfolio URL:</h5>
              <a href={portfolio.portfolioUrl} target="_blank" rel="noopener noreferrer">
                {portfolio.portfolioUrl}
              </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PortfolioGenerator;
