import React, { useState } from 'react';
import { getJobMatch } from '../services/api';

const JobMatching = () => {
  const [jobRole, setJobRole] = useState('');
  const [matchResult, setMatchResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleMatch = async (e) => {
    e.preventDefault();
    
    setLoading(true);
    try {
      const response = await getJobMatch(jobRole);
      setMatchResult(response.data);
    } catch (error) {
      console.error('Match error:', error);
      setMatchResult({ error: 'Failed to get match results' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="job-matching">
      <h2>Job Matching</h2>
      <form onSubmit={handleMatch}>
        <div>
          <label htmlFor="jobRole">Enter Job Role:</label>
          <input
            type="text"
            id="jobRole"
            value={jobRole}
            onChange={(e) => setJobRole(e.target.value)}
            placeholder="e.g., Software Engineer"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Calculating...' : 'Get Match Score'}
        </button>
      </form>
      
      {matchResult && (
        <div className="match-result">
          <h3>Match Result for: {matchResult.jobRole}</h3>
          <p>Match Score: {matchResult.matchScore}%</p>
          
          <h4>Missing Skills:</h4>
          <ul>
            {matchResult.missingSkills.map((skill, index) => (
              <li key={index}>{skill}</li>
            ))}
          </ul>
          
          <h4>Recommended Roles:</h4>
          <ul>
            {matchResult.recommendations.map((job) => (
              <li key={job.id}>
                {job.title} - {job.matchScore}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default JobMatching;
