import React, { useState } from 'react';
import { simulateCareerPath } from '../services/api';

const CareerPathSimulator = () => {
  const [skills, setSkills] = useState('');
  const [desiredRole, setDesiredRole] = useState('');
  const [simulationResult, setSimulationResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSimulate = async (e) => {
    e.preventDefault();
    
    setLoading(true);
    try {
      const response = await simulateCareerPath(skills, desiredRole);
      setSimulationResult(response.data);
    } catch (error) {
      console.error('Simulation error:', error);
      setSimulationResult({ error: 'Failed to simulate career path' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="career-path-simulator">
      <h2>AI Career Path Simulator</h2>
      <form onSubmit={handleSimulate}>
        <div>
          <label htmlFor="skills">Current Skills (comma separated):</label>
          <input
            type="text"
            id="skills"
            value={skills}
            onChange={(e) => setSkills(e.target.value)}
            placeholder="e.g., JavaScript, React, Node.js"
          />
        </div>
        <div>
          <label htmlFor="desiredRole">Desired Role:</label>
          <input
            type="text"
            id="desiredRole"
            value={desiredRole}
            onChange={(e) => setDesiredRole(e.target.value)}
            placeholder="e.g., Software Architect"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Simulating...' : 'Simulate Career Path'}
        </button>
      </form>
      
      {simulationResult && (
        <div className="simulation-result">
          <h3>Career Path for: {simulationResult.desiredRole}</h3>
          <p>Current Skills: {simulationResult.currentSkills.join(', ')}</p>
          
          <h4>Career Progression:</h4>
          <div className="career-steps">
            {simulationResult.careerPath.map((step) => (
              <div key={step.step} className="career-step">
                <h5>Step {step.step}: {step.role} ({step.timeframe})</h5>
                <p>Required Skills: {step.requiredSkills.join(', ')}</p>
                <p>Skills to Acquire: {step.skillsToAcquire.join(', ')}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default CareerPathSimulator;
