import React, { useState } from 'react';
import { checkBias } from '../services/api';

const BiasChecker = () => {
  const [resumeText, setResumeText] = useState('');
  const [checkResult, setCheckResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleCheck = async (e) => {
    e.preventDefault();
    
    setLoading(true);
    try {
      const response = await checkBias(resumeText);
      setCheckResult(response.data);
    } catch (error) {
      console.error('Bias check error:', error);
      setCheckResult({ error: 'Failed to check for bias' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bias-checker">
      <h2>Bias & Inclusivity Checker</h2>
      <form onSubmit={handleCheck}>
        <div>
          <label htmlFor="resumeText">Paste Resume Text:</label>
          <textarea
            id="resumeText"
            value={resumeText}
            onChange={(e) => setResumeText(e.target.value)}
            placeholder="Paste your resume text here for bias checking..."
            rows="10"
            cols="50"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Checking...' : 'Check for Bias'}
        </button>
      </form>
      
      {checkResult && (
        <div className="check-result">
          <h3>Bias Check Results</h3>
          <p>Word Count: {checkResult.wordCount}</p>
          
          {checkResult.biasIssues.length > 0 ? (
            <div>
              <h4>Potential Bias Issues Found:</h4>
              <ul>
                {checkResult.biasIssues.map((word, index) => (
                  <li key={index}>{word}</li>
                ))}
              </ul>
            </div>
          ) : (
            <p>No obvious bias terms detected.</p>
          )}
          
          <h4>Gender Pronoun Usage:</h4>
          <p>Masculine pronouns: {checkResult.genderPronouns.masculine}</p>
          <p>Feminine pronouns: {checkResult.genderPronouns.feminine}</p>
          
          <h4>Suggestions for Improvement:</h4>
          <ul>
            {checkResult.suggestions.map((suggestion, index) => (
              <li key={index}>{suggestion}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default BiasChecker;
