import React, { useState } from 'react';
import { generatePortfolio } from '../services/api';

const PortfolioGenerator = ({ resumeData: propResumeData }) => {
  const [file, setFile] = useState(null);
  const [generating, setGenerating] = useState(false);
  const [portfolio, setPortfolio] = useState(null);
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [extractedData, setExtractedData] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    
    if (selectedFile) {
      // In a real implementation, this would extract data from the resume
      // For now, we'll simulate extracted data
      const simulatedData = {
        name: "John Doe",
        email: "john.doe@example.com",
        phone: "+1 (555) 123-4567",
        skills: "JavaScript, React, Node.js, Python, Machine Learning",
        experience: "5 years of experience in web development and AI",
        education: "B.S. Computer Science, MIT"
      };
      setExtractedData(simulatedData);
      setIsFileUploaded(true);
    }
  };

  const handleGenerate = async (e) => {
    e.preventDefault();
    
    if (!isFileUploaded) {
      alert('Please upload a resume file first');
      return;
    }

    setGenerating(true);
    
    try {
      // Use extracted data or prop data
      const dataToUse = extractedData || propResumeData || {
        name: "Default Name",
        email: "default@example.com",
        phone: "N/A",
        skills: "JavaScript, HTML, CSS",
        experience: "Web development experience",
        education: "Computer Science degree"
      };
      
      const response = await generatePortfolio(dataToUse);
      setPortfolio(response.data);
    } catch (error) {
      console.error('Portfolio generation error:', error);
      setPortfolio({ error: 'Failed to generate portfolio' });
    } finally {
      setGenerating(false);
    }
  };

  return (
    <div className="portfolio-generator">
      <h2>AI Portfolio Generator</h2>
      <form onSubmit={handleGenerate}>
        <div>
          <label htmlFor="resumeFile">Upload Resume File:</label>
          <input
            type="file"
            id="resumeFile"
            accept=".pdf,.doc,.docx"
            onChange={handleFileChange}
          />
          {isFileUploaded && <p>Resume uploaded successfully. Click "Generate Portfolio" to create your portfolio.</p>}
        </div>
        
        <button type="submit" disabled={generating || !isFileUploaded}>
          {generating ? 'Generating Portfolio...' : 'Generate Portfolio'}
        </button>
      </form>
      
      {portfolio && (
        <div className="generated-portfolio">
          <h3>Generated Portfolio</h3>
          <div className="portfolio-content">
            <h4>{portfolio.name}</h4>
            <p>Email: {portfolio.email}</p>
            <p>Phone: {portfolio.phone}</p>
            
            <h5>Skills</h5>
            <ul>
              {portfolio.skills.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
            
            <h5>Experience</h5>
            <p>{portfolio.experience}</p>
            
            <h5>Education</h5>
            <p>{portfolio.education}</p>
            
            <h5>Projects</h5>
            {portfolio.projects.map((project, index) => (
              <div key={index} className="project">
                <h6>{project.title}</h6>
                <p>{project.description}</p>
                <p>Technologies: {project.technologies.join(', ')}</p>
              </div>
            ))}
            
            <div className="portfolio-link">
              <h5>Your Portfolio URL:</h5>
              <a href={portfolio.portfolioUrl} target="_blank" rel="noopener noreferrer">
                {portfolio.portfolioUrl}
              </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PortfolioGenerator;
