import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export const uploadResume = (formData) => {
  return api.post('/resume/upload', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });
};

export const getJobMatch = (jobRole) => {
  return api.post('/job/match', { jobRole });
};

export const simulateCareerPath = (skills, desiredRole) => {
  return api.post('/career/simulate', { skills, desiredRole });
};

export const predictFutureSkills = (skills) => {
  return api.post('/skills/predict', { skills });
};

export const checkBias = (resumeText) => {
  return api.post('/bias/check', { resumeText });
};

export const generatePortfolio = (resumeData) => {
  return api.post('/portfolio/generate', resumeData);
};

export default api;
